rules.formula:
AND (ISCHANGED(Comm_Status__c),
     OR ( 
         ISPICKVAL(Comm_Status__c, "Awaiting User Response"),
         ISPICKVAL(Comm_Status__c, "User Response Received")
        )
)

OR(
/* New Case, and the account is not Bluescape HQ */
AND(ISNEW(), NOT ISBLANK(AccountId),NOT(AccountId = "001E000000RrswS"))
,
/* Account is changed, it's not blank and not Bluescape HQ, and case is open */
AND(ISCHANGED(AccountId),NOT(AccountId = "001E000000RrswS"),NOT ISBLANK(AccountId),NOT(IsClosed))
)
